  <script>
  document.getElementById('appointmentForm').addEventListener('submit', async function(event) {
    event.preventDefault(); // Prevent default form submission

    // Collect form data
    const formData = new FormData(this);
    const formObject = Object.fromEntries(formData.entries()); // Ensure compatibility with all fields

    try {
      const response = await fetch('https://physiofitnessrajkot.com/api/store-appointment', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formObject),
      });

      const result = await response.json();

      if (response.ok) {
        alert('Appointment created successfully');
        this.reset(); // Clear form fields on success
      } else {
        alert('Failed to create appointment. ' + (result.message || 'Unknown error occurred.'));
      }
    } catch (error) {
      console.error('Error:', error);
      alert('An error occurred. Please try again later.');
    }
  });
</script>
